import jwt from "jsonwebtoken";

/**
 * Decodes a jwt generated token down to the employee it was
 * generated from
 *
 * @param {string} token A token generated by jwt
 * @returns {string} The employee number the jwt was generated from
 */
const decodeToken = (token) => jwt.verify(token, process.env.JWT_SECRET);

export default decodeToken;
